using System.Collections;using System.Collections.Generic;using UnityEngine;public class DiveProjectile : MonoBehaviour{    public float projectileSpeed;    public float projectileDistance;    private float initialX;    void Awake()    {            }    // Start is called before the first frame update    void Start()    {        initialX = transform.position.x;    }    // Update is called once per frame    void Update()    {            }    void FixedUpdate()    {        transform.Translate(Vector3.right * projectileSpeed);        if (Mathf.Abs(transform.position.x - initialX) > projectileDistance)        {            Destroy(gameObject);        }    }    void OnTriggerEnter2D(Collider2D other)    {        Destroy(gameObject);        other.SendMessage("OnPress");    }}